package basics;

public class Fac {
  public static void main(String args[])
  {
	  Fac fact=new Fac();
	  fact.print();
		// TODO Auto-generated method stub
		System.out.println(fac);
  }

  
  
  static int fac=1;
  int i=1;
private void print() {

	while(i<=5)
	{
		fac=fac*i;
		i++;
	}

}

	
	}


